import './main-view.scss'

import {Component} from 'react'

import USAMap from "react-usa-map";

export default class MainView extends Component{

    state ={
        activeState: null
    }

    handleClick = (event) => {
        this.setState({activeState: event.target.dataset.name})
    }

    renderStormInfo = () => {
        if(!this.state.activeState){
            return(
                <div className="intructions">
                    Select a state to see hurricanes that made landfall there.
                </div>
            )
        }

        fetch("http://localhost:9999/hurricane-data?state=FL&land-fall-date&name&max-wind-speed")
        .then(data => console.log(data))


        return(
            <div className="storm-data">
                <h5> {this.state.activeState}</h5>


            </div>
        )
    }

    statesCustomConfig = () => {
        if(!this.state.activeState) return
        return {
          [this.state.activeState] : {
              fill: "navy"
          }
        };
      };


    render(){
        console.log(this.state.activeState)
        return(
            <div className="main-view">
                <div className="header">
                    <h1>Karen Clark Interview Question</h1>
                    <h4> Huricane Report by state</h4>
                </div>


                <USAMap customize={this.statesCustomConfig()} 
                    onClick={this.handleClick}>

                </USAMap>

                <div className="data-display">

                    {this.renderStormInfo()}

                </div>


            </div>

        )
    }

}
